import { useState } from "react";
import "./App.css";
function App() {
  const initialArray = [
    "apple",
    "banana",
    "cherry",
    "date",
    "elderberry",
  ];
  const [array, setArray] = useState(initialArray);
  const [result, setResult] = useState("");
  const [query, setQuery] = useState("");
  /** 함수들 */
  const handleForEach = () => {
    let tempResult = "";
    array.forEach(function (fruit) {
      tempResult += `${fruit} ,`;
    });
    // tempResult = tempResult.slice(0, -2);
    // setResult(tempResult);
    setResult(tempResult.slice(0, -2));
  };
  const handleFilter = () => {
    const filteredList = array.filter(function (fruit) {
      // 얘를 필터링을 할지 말지를 결정한다.
      if (fruit.includes(query)) {
        return true;
      } else {
        return false; // 여기서 결정한다.
      }
    });
    setResult(filteredList.join(","));
  };
  const handleMap = () => {
    // array를 대문자로 변환하여 출력
    const mappedList = array.map(function (fruit) {
      return fruit.toUpperCase();
    });
    setResult(mappedList.join(","));
  };
  const handleReduce = () => {
    const reducedListText = array.reduce(function(acc, cur) {
        return `${acc}, ${cur}`;
    });
    setResult(reducedListText);
  };

  const handlePush = () => {
    if(!query) {
      alert("값이 없습니다")
      return false;
    }
    const newArr = [...array, query];
    setArray(newArr);
    setResult(newArr.join(","))
  }
  const handlePop = () => {
const newArr = [...array];
newArr.pop();
setArray(newArr);
setResult(newArr.join(","))
  };

  const handleslice = () => {
    const newArr = [...array];
    const slicdArr = newArr.slice(0,3);
    setArray(slicdArr);
    setResult(slicdArr.join(","))
  }

  const handlesplice = () => {
    const newArr = ['apple','kiwi','lime',...array];
    newArr.splice(3,3)
    setArray(newArr);
    setResult(newArr.join(","))
  }
  const handleindexOf = () => {
    const newArr = [,...array];
    newArr.indexOf(initialArray)
    setArray(newArr)
    setResult(newArr)
  }




  return (
    <div className="st-API">
      <div className="title">
      <h1>Standard반 배열 API</h1>
      </div>
      <div className="textInput">
        텍스트를 입력하세요 :
        <input
          value={query}
          onChange={function (e) {
            setQuery(e.target.value);
          }}
          />

      </div>
      <div className="buttons">
        <button onClick={handleForEach}>forEach</button>
        <button onClick={handleFilter}>filter</button>
        <button onClick={handleMap}>map</button>
        <button onClick={handleReduce}>Reduce</button>
        <button onClick={handlePush}>Push</button>
        <button onClick={handlePop}>Pop</button>
        <button onClick={handleslice}>slice</button>
        <button onClick={handlesplice}>splice</button>
        <button onClick={handleindexOf}>indexOf</button>


      </div>
      <div className="ini-Arr">
        <strong>원본배열:</strong> : {array.join(",")}
      </div>
      <div className="results">
        <strong>결과물:</strong> : {result}
      </div>
    </div>
  );
}
export default App;
